INSERT INTO `usernotes` VALUES (2,'学习Java','hellolllllll','1499514580','common','15813246678@163.com','0'),(30,'phpexcel不能输出标点符号以及数字开头的字符串问题','&lt;p style=&quot;font-size:14px;&quot;&gt;\r\n	用一下这句代码解决：\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;&quot;&gt;\r\n	$objSheet-&amp;gt;setCellValueExplicit($zm.$j,$row1[$res[3][$i]],PHPExcel_Cell_DataType::TYPE_STRING);\r\n&lt;/p&gt;\r\n&lt;div style=&quot;font-size:14px;&quot;&gt;\r\n	填充单元格的值以及设置单元格为纯文本格式！\r\n&lt;/div&gt;','1499952689','PHP开发经验','1126089177@qq.com','0'),(31,'mysql不支持插入双号解决办法','&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;使用htmlspecialchars函数进行转义，需添加第二个参数：&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;htmlspecialchars($htmlData,ENT_QUOTES);&lt;/span&gt;\r\n&lt;/p&gt;','1499952746','PHP开发经验','1126089177@qq.com','0'),(32,'mysql常用语句','&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;1连接数据库&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;mysql -uroot -p&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;2创建数据库&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;create database mydatabase&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;nbsp;DEFAULT CHARSET utf8 COLLATE utf8_general_ci;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;3创建表&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;create table `mytable`(`id` int(10) auto_increment primary key,`name` varchar(20) not null)ENGINE=InnoDB CHARACTER SET utf8 COLLATE utf8_general _ci;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;4备份数据库&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;mysqldump -hlocalhost -uroot -p123456 mydatabase &amp;gt; mydatabase.sql;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;5导入sql数据&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;use database&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;source mydatabase.sql;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;6删除表&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;drop mytable;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;7清空表&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;delete from mytable;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	8查看表结构\n&lt;/p&gt;\n&lt;p&gt;\n	desc mytable\n&lt;/p&gt;\n&lt;div&gt;\n	&lt;br /&gt;\n&lt;/div&gt;','1499952790','PHP开发经验','1126089177@qq.com','0'),(33,'上传文件时出现空白','&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;解决办法：&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;需要在form标签添加enctype=&quot;multipart/form-data&quot;&lt;/span&gt;\r\n&lt;/p&gt;','1499952893','PHP开发经验','1126089177@qq.com','0'),(34,'php出现500错误，但不提示具体错误解决办法','&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;deepin 和debian系统下找到&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;/etc/php5/apache2/php.ini文件，然后将相关行修改为&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;error_reporting = E_ALL &amp;amp; ~E_DEPRECATED &amp;amp; ~E_STRICT&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;display_errors = On&lt;/span&gt; \n&lt;/p&gt;','1499952937','PHP开发经验','1126089177@qq.com','0'),(35,'php分页类','&lt;span style=&quot;font-size:16px;&quot;&gt;class Page&lt;/span&gt;&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;{&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;//第一个参数为传入的页码&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;//第二个参数为每页显示的数量&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;//第三个参数为数据的数量&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; public function getLimit($page,$pagesize, $datanum)&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; {&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $page_size = $pagesize;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $totalnums = $datanum;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if ($page &amp;lt; 1 || !is_numeric($page)) {&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $page = 1;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if ($totalnums == 0) {&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $limit = &quot;&quot;;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $last_page = $page-1;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $next_page = $page+1;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $totalpages = 0;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; return array(&quot;limit&quot;=&amp;gt;$limit,&quot;last_page&quot;=&amp;gt;$last_page,&quot;next_page&quot;=&amp;gt;$next_page,&quot;total_pages&quot;=&amp;gt;$totalpages);&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; } else {&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $totalpages = ceil($totalnums / $page_size);&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if ($page &amp;gt; $totalpages) {&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $page = $totalpages;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $startindex = ($page - 1) * $page_size;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $limit = &quot;limit {$startindex},$page_size&quot;;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $last_page = $page-1;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $next_page = $page+1;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; return array(&quot;limit&quot;=&amp;gt;$limit,&quot;last_page&quot;=&amp;gt;$last_page,&quot;next_page&quot;=&amp;gt;$next_page,&quot;total_pages&quot;=&amp;gt;$totalpages);&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; &amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;}&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;//调用getlimit()函数将获得查询数据库的limitl,页码的上一页和下一页以及总页数&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;','1499952962','PHP开发经验','1126089177@qq.com','0'),(36,'ajax上传文件并实时反馈','&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;//利用formdata上传文件&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;function upload() {&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; //检查是否已经选择文件&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; var str = document.getElementById(\'file\').value.length;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; if(str==0){&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; alert(&quot;您没有选择任何文件,请点击“选择文件”进行选取！&quot;);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; return false;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; }else{&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //创建formdata对象&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; var fd = new FormData();&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; fd.append(&quot;file&quot;,$(&quot;#file&quot;)[0].files[0]);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; var xhr = new XMLHttpRequest();&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //文件传输时事件&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; xhr.upload.onprogress = function (event) {&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if(event.lengthComputable){&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; var percent = Math.round(event.loaded*100 / event.total);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; console.log(\'%d%\',percent);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;#progress&quot;).css(&quot;width&quot;,percent+&quot;%&quot;);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;#progress&quot;).text(percent+&quot;%&quot;);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; };&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //文件开始传输时事件&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; xhr.onloadstart = function (event) {&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(\'#mymodal\').modal(&quot;show&quot;);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;#closeprogress&quot;).hide();&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;#stop&quot;).one(&quot;click&quot;,function () {&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; xhr.abort();&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; })&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; loading(false);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; };&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //文件上传完成时事件&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; xhr.onload = function (event) {&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;.info&quot;).text(xhr.responseText);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;#closeprogress&quot;).show();&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;#stop&quot;).hide();&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; };&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //发生错误时触发的事件&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; xhr.onerror = function (event) {&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;.info&quot;).text(&quot;上传发生错误，请重试！&quot;);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //ajax被取消时触发的事件&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; xhr.onabort = function (event) {&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;.info&quot;).text(&quot;用户已终止&quot;);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&quot;#closeprogress&quot;).show();&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //发送请求&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; xhr.open(&quot;post&quot;,&quot;receive.php&quot;,true);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; xhr.send(fd);&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;&amp;nbsp; &amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:16px;font-family:SimSun;&quot;&gt;}&lt;/span&gt;','1499953126','PHP开发经验','1126089177@qq.com','0'),(37,'使用get方法在url上传递数据需要注意的问题','&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;1传输时尽量使用单词和数字，避免使用#$*+等危险符号，这些符号也会被自动转义。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;2尽量不使用中文，因为中文解密后容易出现乱码。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;3如果要使用到含中英文的文件名，可以通过传递相关数据的id，然后通过id读出文件名，然后拿来使用。&lt;/span&gt;\r\n&lt;/p&gt;','1499953293','PHP开发经验','1126089177@qq.com','0'),(38,'edusoho网校系统在winserver2008安装时提示某个文件缺失问题','&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;原因：window server2008压缩软件版本太久，解压之后导致某些文件缺失。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;解决办法：更新压缩工具或者更换压缩工具进行解压。&lt;/span&gt;\r\n&lt;/p&gt;','1499953323','PHP开发经验','1126089177@qq.com','0'),(39,'Centos6.5配置php+mysql+php过程','&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;centos 6.5系统安装配置LAMP(Apache+PHP5+MySQL)服务器环境&lt;/span&gt;&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;准备篇：&lt;/span&gt;&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;1、配置防火墙，开启80端口、3306端口&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;vi /etc/sysconfig/iptables&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;-A INPUT -m state --state NEW -m tcp -p tcp --dport 80 -j ACCEPT #允许80端口通过防火墙&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;-A INPUT -m state --state NEW -m tcp -p tcp --dport 3306 -j ACCEPT #允许3306端口通过防火墙&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/iptables restart #重启防火墙使配置生效&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;2、关闭SELINUX&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;vi /etc/selinux/config&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;#SELINUX=enforcing #注释掉&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;#SELINUXTYPE=targeted #注释掉&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;SELINUX=disabled #增加&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;:wq!#保存退出&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;shutdown -r now#重启系统&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;安装篇：&lt;/span&gt;&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;一、安装Apache&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;yum install httpd #根据提示，输入Y安装即可成功安装&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/httpd start#启动Apache&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;备注：Apache启动之后会提示错误：&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;正在启动 httpd:httpd: Could not reliably determine the server&#039;s fully qualif domain name, using ::1 for ServerName&lt;/span&gt;&lt;br /&gt;\r\n&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;解决办法：&lt;/span&gt;&lt;/strong&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;vi /etc/httpd/conf/httpd.conf #编辑&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;找到 #ServerName www.example.com:80&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;修改为 ServerName www.jbaobao.net:80 #这里设置为你自己的域名，如果没有域名，可以设置为localhost&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;:wq! #保存退出&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;chkconfig httpd on #设为开机启动&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/httpd restart #重启Apache&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;二、安装MySQL&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;1、安装MySQL&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;yum install&amp;nbsp;mysql&amp;nbsp;mysql-server #询问是否要安装，输入Y即可自动安装,直到安装完成&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/mysqld start #启动MySQL&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;chkconfig mysqld on #设为开机启动&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;cp /usr/share/mysql/my-medium.cnf /etc/my.cnf #拷贝配置文件（注意：如果/etc目录下面默认有一个my.cnf，直接覆盖即可）&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;2、为root账户设置密码&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;mysql_secure_installation&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;回车，根据提示输入Y&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;输入2次密码，回车&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;根据提示一路输入Y&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;最后出现：Thanks for using MySQL!&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;MySql密码设置完成，重新启动 MySQL：&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/mysqld restart #重启&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/mysqld stop #停止&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/mysqld start #启动&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;三、安装PHP5&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;1、安装PHP5&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;系统运维 温馨提醒：qihang01原创内容版权所有,转载请注明出处及原文链接&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;yum install php #根据提示输入Y直到安装完成&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;2、安装PHP组件，使 PHP5 支持 MySQL&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;yum install php-mysql php-gd libjpeg* php-imap php-ldap php-odbc php-pear php-xml php-xmlrpc php-mbstring php-mcrypt php-bcmath php-mhash libmcrypt #这里选择以上安装包进行安装，根据提示输入Y回车&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/mysqld restart #重启MySql&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/httpd restart #重启Apche&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;配置篇&lt;/span&gt;&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;一、Apache配置&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;vi /etc/httpd/conf/httpd.conf #编辑文件&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;ServerTokens OS　 #在44行 修改为：ServerTokens Prod （在出现错误页的时候不显示服务器操作系统的名称）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;ServerSignature On　 #在536行 修改为：ServerSignature Off （在错误页中不显示Apache的版本）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;Options Indexes FollowSymLinks　 #在331行 修改为：Options Includes ExecCGI FollowSymLinks（允许服务器执行CGI及SSI，禁止列出目录）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;#AddHandler cgi-script .cgi　#在796行 修改为：AddHandler cgi-script .cgi .pl （允许扩展名为.pl的CGI脚本运行）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;AllowOverride None　 #在338行 修改为：AllowOverride All （允许.htaccess）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;AddDefaultCharset UTF-8　#在759行 修改为：AddDefaultCharset GB2312　（添加GB2312为默认编码）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;　 Options Indexes MultiViews FollowSymLinks #在554行 修改为 Options MultiViews FollowSymLinks（不在浏览器上显示树状目录结构）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;DirectoryIndex index.html index.html.var #在402行 修改为：DirectoryIndex index.html index.htm Default.html Default.htm index.php Default.php index.html.var （设置默认首页文件，增加index.php）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;KeepAlive Off #在76行 修改为：KeepAlive On （允许程序性联机）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;MaxKeepAliveRequests 100 #在83行 修改为：MaxKeepAliveRequests 1000 （增加同时连接数）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;:wq! #保存退出&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/httpd restart#重启&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;系统运维 温馨提醒：qihang01原创内容版权所有,转载请注明出处及原文链接&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;rm -f /etc/httpd/conf.d/welcome.conf /var/www/error/noindex.html #删除默认测试页&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;二、php配置&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;vi /etc/php.ini #编辑&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;date.timezone = PRC #在946行 把前面的分号去掉，改为date.timezone = PRC&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;disable_functions = passthru,exec,system,chroot,scandir,chgrp,chown,shell_exec,proc_open,proc_get_status,ini_alter,ini_alter,ini_restore,dl,openlog,syslog,readlink,symlink,popepassthru,stream_socket_server,escapeshellcmd,dll,popen,disk_free_space,checkdnsrr,checkdnsrr,getservbyname,getservbyport,disk_total_space,posix_ctermid,posix_get_last_error,posix_getcwd, posix_getegid,posix_geteuid,posix_getgid, posix_getgrgid,posix_getgrnam,posix_getgroups,posix_getlogin,posix_getpgid,posix_getpgrp,posix_getpid, posix_getppid,posix_getpwnam,posix_getpwuid, posix_getrlimit, posix_getsid,posix_getuid,posix_isatty, posix_kill,posix_mkfifo,posix_setegid,posix_seteuid,posix_setgid, posix_setpgid,posix_setsid,posix_setuid,posix_strerror,posix_times,posix_ttyname,posix_uname&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;#在386行 列出PHP可以禁用的函数，如果某些程序需要用到这个函数，可以删除，取消禁用。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;expose_php = Off #在432行 禁止显示php版本的信息&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;magic_quotes_gpc = On #在745行 打开magic_quotes_gpc来防止SQL注入&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;short_open_tag = ON #在229行支持php短标签&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;open_basedir = .:/tmp/ #在380行 设置表示允许访问当前目录(即PHP脚本文件所在之目录)和/tmp/目录,可以防止php木马跨站,如果改了之后安装程序有问题，可以注销此行，或者直接写上程序的目录/data/www.osyunwei.com/:/tmp/&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;:wq! #保存退出&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/mysqld restart #重启MySql&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;/etc/init.d/httpd restart #重启Apche&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;测试篇&lt;/span&gt;&lt;/strong&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;cd /var/www/html&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;vi index.php #编辑输入下面内容&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&amp;lt;?php&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;phpinfo();&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;?&amp;gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;:wq! #保存退出&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;在客户端浏览器输入服务器IP地址，可以看到相关的配置信息！&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&amp;nbsp;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;phpMyAdmin常见问题&lt;/span&gt;&lt;br /&gt;\r\n&lt;/strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;1. phpmyadmin提示未开启php-mcrypt,这是因为没有安装php-mrcrpt扩展。在官方源没有php-mrcrpt扩展，请安装epel源后再yum install php-mcrypt&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;2. phpmyadmin无法访问&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;phpmyadmin默认只能使用http://localhost/phpmyadmin来访问，可以修改/etc/httpd/conf.d/phpmyadmin.conf中对应部分为&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&amp;lt;Directory &quot;/usr/share/phpmyadmin&quot;&amp;gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;Order Deny,Allow&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;# Deny from all&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;Allow from All&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&amp;lt;/Directory&amp;gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;不过强烈不支持这么做的，把phpmyadmin暴露了不安全&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;注意：&lt;/span&gt;&lt;br /&gt;\r\n&lt;/strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;apache解析php，修改httpd.conf文件，添加&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;Addtype application/x-httpd-php .php .phtml&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;Addtype application/x-httpd-php-source .phps&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:tahoma, 宋体;font-size:14px;text-align:justify;background-color:#FAFAFC;&quot;&gt;\r\n	&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;myql可以远程登录&lt;/span&gt;&lt;br /&gt;\r\n&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;设置mysql密码&lt;/span&gt;&lt;br /&gt;\r\n&lt;/strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;mysql&amp;gt;; USE mysql;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;mysql&amp;gt;; UPDATE user SET Password=PASSWORD(&#039;newpassword&#039;) WHERE user=&#039;root&#039;;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;mysql&amp;gt;; FLUSH PRIVILEGES;&lt;/span&gt;&lt;br /&gt;\r\n&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;1.2.3 允许远程登录&lt;/span&gt;&lt;br /&gt;\r\n&lt;/strong&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;mysql -u root -p&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;Enter Password: &amp;lt;your new password&amp;gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;mysql&amp;gt;GRANT ALL PRIVILEGES ON *.* TO &#039;用户名&#039;@&#039;%&#039; IDENTIFIED BY &#039;密码&#039; WITH GRANT OPTION;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;完成后就能用mysql-front远程管理mysql了。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;设为开机启动&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;chkconfig mysqld on&lt;/span&gt;\r\n&lt;/p&gt;','1499953398','环境配置','1126089177@qq.com','0'),(40,'wampserver修改mysql密码','&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2、进入控制台后，提示输入密码(不用输入任何密码，因为密码为空)，按回车键进入;&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;　　3、输入“USE mysql;”然后回车，意思是选择mysql数据库;&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;　　4、输入“update user set password=password(&quot;4370292301erplove&quot;) where user=\'root\';”然后回车;返回信息：&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;　　Query OK, 0 rows affected (0.00 sec)&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;　　Rows matched: 2 Changed: 0 Warnings: 0&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;　　5、输入quit退出控制台;&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;　　6、重启mysql服务;&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;　　7、输入用户名、你设置定的密码，登录成功。&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;　　这样mysql登录与密码是没问题了，但是使用phpmyadmin登录不了，提醒密码无效了，这个因为我们还有一步这完成&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;　　这样仅仅是修改了mysql的密码，如果访问phpmyadmin，会提示你权限问题之类。找到phpmyadmin安装目录，一般是在wamp/apps/phpmyadmin/这个位置，打开config.inc.php，编辑如下几行：&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;代码如下&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;$cfg[\'Servers\'][$i][\'auth_type\'] = \'http\';//由config修改为http&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;$cfg[\'Servers\'][$i][\'user\'] = \'root\';&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;$cfg[\'Servers\'][$i][\'password\'] = \'newpassword\';&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;$cfg[\'Servers\'][$i][\'AllowNoPassword\'] = false;&lt;/span&gt; \n&lt;/div&gt;','1499953611','环境配置','1126089177@qq.com','0'),(41,'debian8.2安装apache2+php5.6+mysql环境','&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;1安装apache2&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;apt-get install apache2&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;设置开机&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;启动：s&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;ystemct&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;l enable apache2&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;安装php5.6&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;，debian8.2&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;默认手动安装的&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;是php5.6版本&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;apt&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;-get i&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;nstall&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;php5&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;apt-get install&amp;nbsp;&lt;span style=&quot;color:#333333;font-family:&amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;libapache2-mod-php5&lt;/span&gt;&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;3安装mysql&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;apt-get install mysql-server&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;在弹出的界面中输入需要设置的密码&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;设置开机启动：systemctl enable mysql&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;4安装php5-mysql php5-curl扩展&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;apt-get install php5-mysql&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;apt-get install php5-curl&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;最后重启apache2进行测试是否配置成功&lt;/span&gt; \n&lt;/p&gt;','1500032700','环境配置','1126089177@qq.com','0'),(42,'centos6.5下apache配置https','&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;1安装ssl_mod&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;默认不开启ssl扩展,需要输入命令yum install -y ssl_mod 来安装&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2申请ssl证书,以腾讯云为例&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;将申请到的三个文件:1_root_bundle.crt&amp;nbsp; 2_www.aibasil.cn.crt&amp;nbsp; 3_www.aibasil.cn.key复制到/var/www/ssl目录&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;3编辑/etc/httpd/conf.d/ssl.conf文件,将相应的行修改为如下所示&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;lt;VirtualHost _default_:443&amp;gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;DocumentRoot &quot;/var/www/html&quot;&lt;/span&gt;&lt;br /&gt;\r\n&lt;strong&gt;&lt;span style=&quot;background-color:#FFE500;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;ServerName www.aibasil.cn&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;SSLCertificateFile&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;background-color:#FFE500;font-size:16px;&quot;&gt;/var/www/ssl/2_www.aibasil.cn.crt&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;SSLCertificateKeyFile&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;background-color:#FFE500;font-size:16px;&quot;&gt;/var/www/ssl/3_www.aibasil.cn.key&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#FFE500;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;font-size:16px;&quot;&gt;SSLCertificateChainFile&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;/var/www/ssl/1_root_bundle.crt&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#FFE500;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;font-size:16px;&quot;&gt;&amp;lt;/VirtualHost&amp;gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;重启apache&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n&lt;/p&gt;','1500032726','环境配置','1126089177@qq.com','0'),(43,'apache2 开启rewrite功能','&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;在配置laravel时,发现直接输入域名访问(不带index.php后缀)时,url函数失效,原因是没有开启apache2 的rewrite模块。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;于是找到了apache2开启该模块的方法，在deepin下，启动终端切换到root模式，然后输入以下命令进行开启：&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;a2enmod rewrite&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;然后重启apache&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;systemctl restart apache2&lt;/span&gt;\r\n&lt;/p&gt;','1500032905','环境配置','1126089177@qq.com','0'),(45,'公钥与密钥','&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span style=&quot;line-height:2;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:2;&quot;&gt;加密和认证&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　首先我们需要区分加密和认证这两个基本概念。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　 　加密是将数据资料加密，使得非法用户即使取得加密过的资料，也无法获取正确的资料内容，所以数据加密可以保护数据，防止监听攻击。其重点在于数据的安全 性。身份认证是用来判断某个身份的真实性，确认身份后，系统才可以依不同的身份给予不同的权限。其重点在于用户的真实性。两者的侧重点是不同的。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　公钥和私钥&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　其次我们还要了解公钥和私钥的概念和作用。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　在现代密码体制中加密和解密是采用不同的密钥（公开密钥），也就是非对称密钥密码系统，每个通信方均需要两个密钥，即公钥和私钥，这两把密钥可以互为加解密。公钥是公开的，不需要保密，而私钥是由个人自己持有，并且必须妥善保管和注意保密。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　公钥私钥的原则：&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;一个公钥对应一个私钥。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;密钥对中，让大家都知道的是公钥，不告诉大家，只有自己知道的，是私钥。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;如果用其中一个密钥加密数据，则只有对应的那个密钥才可以解密。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;如果用其中一个密钥可以进行解密数据，则该数据必然是对应的那个密钥进行的加密。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　非对称密钥密码的主要应用就是公钥加密和公钥认证，而公钥加密的过程和公钥认证的过程是不一样的，下面我就详细讲解一下两者的区别。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　基于公开密钥的加密过程&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　比如有两个用户Alice和Bob，Alice想把一段明文通过双钥加密的技术发送给Bob，Bob有一对公钥和私钥，那么加密解密的过程如下：&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;Bob将他的公开密钥传送给Alice。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;Alice用Bob的公开密钥加密她的消息，然后传送给Bob。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;Bob用他的私人密钥解密Alice的消息。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　上面的过程可以用下图表示，Alice使用Bob的公钥进行加密，Bob用自己的私钥进行解密。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　基于公开密钥的认证过程&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　　身份认证和加密就不同了，主要用户鉴别用户的真伪。这里我们只要能够鉴别一个用户的私钥是正确的，就可以鉴别这个用户的真伪。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;　 　还是Alice和Bob这两个用户，Alice想让Bob知道自己是真实的Alice，而不是假冒的，因此Alice只要使用私钥对文件签名发送 给Bob，Bob使用Alice的公钥对文件进行解密，如果可以解密成功，则证明Alice的私钥是正确的，因而就完成了对Alice的身份鉴别。整个身 份认证的过程如下：&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;Alice用她的私人密钥对文件加密，从而对文件签名。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;Alice将签名的文件传送给Bob。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:14px;line-height:2;&quot;&gt;Bob用Alice的公钥解密文件，从而验证签名。&lt;/span&gt;','1500032979','加密与解密','1126089177@qq.com','0'),(46,'利用clipboard.js插件实现复制内容到粘贴板','&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;首先下载&lt;/span&gt;&lt;span style=&quot;background-color:#E4E4FF;color:#008000;font-weight:bold;font-size:14px;&quot;&gt;clipboard.js&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;nbsp;插件，然后&lt;span style=&quot;background-color:#FFE500;&quot;&gt;在html文件引入&lt;/span&gt;，具体实现如下：&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;pre class=&quot;prettyprint lang-html&quot;&gt;&amp;lt;!DOCTYPE html&amp;gt;\r\n&amp;lt;html lang=&quot;en&quot;&amp;gt;\r\n&amp;lt;head&amp;gt;\r\n    &amp;lt;meta charset=&quot;UTF-8&quot;&amp;gt;\r\n    &amp;lt;title&amp;gt;cp&amp;lt;/title&amp;gt;\r\n    &amp;lt;script src=&quot;public/js/clipboard.min.js&quot;&amp;gt;&amp;lt;/script&amp;gt;\r\n&amp;lt;/head&amp;gt;\r\n\r\n&amp;lt;body&amp;gt;\r\n&amp;lt;button class=&quot;btn&quot;  data-clipboard-target=&quot;#info&quot;&amp;gt;复制&amp;lt;/button&amp;gt;\r\n&amp;lt;span id=&quot;info&quot;&amp;gt;这里的内容将被复制&amp;lt;/span&amp;gt;\r\n&amp;lt;/body&amp;gt;\r\n&amp;lt;script type=&quot;text/javascript&quot;&amp;gt;\r\n    //复制分享链接\r\n        var clipboard = new Clipboard(&quot;.btn&quot;);\r\n        clipboard.on(&#039;success&#039;, function(e) {\r\n            alert(&quot;复制成功！&quot;);\r\n        });\r\n\r\n        clipboard.on(&#039;error&#039;, function(e) {\r\n            alert(&quot;复制失败！&quot;);\r\n        });\r\n&amp;lt;/script&amp;gt;\r\n&amp;lt;/html&amp;gt;&lt;/pre&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;','1500033004','JavaScript','1126089177@qq.com','0'),(47,'利用jqueryui的拖拽和放置功能处理后保存位置的方法','&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;拖放之后利用浏览器的审查元素查看代码，然后复制整个html内容，再新建另外一个html粘贴。&lt;/span&gt;','1500033027','JavaScript','1126089177@qq.com','0'),(48,'vim命令详解','&lt;h1 class=&quot;postTitle&quot;&gt;\n	&lt;a id=&quot;cb_post_title_url&quot; class=&quot;postTitle2&quot; href=&quot;http://www.cnblogs.com/usergaojie/p/4583796.html&quot;&gt;vim 命令详解&lt;/a&gt; \n&lt;/h1&gt;\n&lt;div class=&quot;clear&quot; style=&quot;font-family:Helvetica, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\n&lt;/div&gt;\n&lt;div class=&quot;postBody&quot; style=&quot;font-size:14px;color:#444444;font-family:Helvetica, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\n	&lt;div id=&quot;cnblogs_post_body&quot;&gt;\n		&lt;p&gt;\n			vi: Visual Interface 可视化接口&lt;br /&gt;\nvim: VI iMproved	VI增强版\n		&lt;/p&gt;\n		&lt;p&gt;\n			全屏编辑器，模式化编辑器\n		&lt;/p&gt;\n		&lt;p&gt;\n			&lt;strong&gt;vim模式：&lt;/strong&gt; \n		&lt;/p&gt;\n		&lt;ul&gt;\n			&lt;li&gt;\n				编辑模式(命令模式)\n			&lt;/li&gt;\n			&lt;li&gt;\n				输入模式\n			&lt;/li&gt;\n			&lt;li&gt;\n				末行模式\n			&lt;/li&gt;\n		&lt;/ul&gt;\n		&lt;p&gt;\n			&lt;strong&gt;模式转换：&lt;/strong&gt; \n		&lt;/p&gt;\n		&lt;p&gt;\n			编辑--&amp;gt;输入：\n		&lt;/p&gt;\n&lt;pre&gt;    i: 在当前光标所在字符的前面，转为输入模式；\n\n    a: 在当前光标所在字符的后面，转为输入模式；\n\n    o: 在当前光标所在行的下方，新建一行，并转为输入模式；\n\n    I：在当前光标所在行的行首，转换为输入模式\n\n    A：在当前光标所在行的行尾，转换为输入模式\n\n    O：在当前光标所在行的上方，新建一行，并转为输入模式；\n&lt;/pre&gt;\n		&lt;p&gt;\n			输入--&amp;gt;编辑：\n		&lt;/p&gt;\n&lt;pre&gt;    ESC&lt;/pre&gt;\n		&lt;p&gt;\n			编辑--&amp;gt;末行：\n		&lt;/p&gt;\n&lt;pre&gt;    :&lt;/pre&gt;\n		&lt;p&gt;\n			末行--&amp;gt;编辑：\n		&lt;/p&gt;\n&lt;pre&gt;    ESC, ESC&lt;/pre&gt;\n		&lt;div style=&quot;background-color:#DDDDDD;padding:10px;color:red;&quot;&gt;\n			注：输入模式和末行模式之间不能直接切换\n		&lt;/div&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			一、打开文件\n		&lt;/h3&gt;\n&lt;pre&gt;    vim +# :打开文件，并定位于第#行\n\n    vim +：打开文件，定位至最后一行\n\n    vim +/PATTERN : 打开文件，定位至第一次被PATTERN匹配到的行的行首\n&lt;/pre&gt;\n		&lt;div style=&quot;background-color:#DDDDDD;padding:10px;color:red;&quot;&gt;\n			注：默认处于编辑模式\n		&lt;/div&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			二、关闭文件\n		&lt;/h3&gt;\n		&lt;p&gt;\n			1、末行模式关闭文件\n		&lt;/p&gt;\n&lt;pre&gt;    :q  退出\n\n    :wq 保存并退出\n\n    :q! 不保存并退出\n\n    :w 保存\n\n    :w! 强行保存\n\n    :wq --&amp;gt; :x\n&lt;/pre&gt;\n		&lt;p&gt;\n			2、编辑模式下退出\n		&lt;/p&gt;\n&lt;pre&gt;ZZ: 保存并退出&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			三、移动光标(编辑模式)\n		&lt;/h3&gt;\n		&lt;p&gt;\n			1、逐字符移动：\n		&lt;/p&gt;\n&lt;pre&gt;    h: 左\n\n    l: 右\n\n    j: 下\n\n    k: 上\n\n    #h: 移动#个字符\n&lt;/pre&gt;\n		&lt;p&gt;\n			2、以单词为单位移动\n		&lt;/p&gt;\n&lt;pre&gt;    w: 移至下一个单词的词首\n\n    e: 跳至当前或下一个单词的词尾\n\n    b: 跳至当前或前一个单词的词首\n\n    #w: 移动#个单词\n&lt;/pre&gt;\n		&lt;p&gt;\n			3、行内跳转：\n		&lt;/p&gt;\n&lt;pre&gt;    0: 绝对行首\n\n    ^: 行首的第一个非空白字符\n\n    $: 绝对行尾\n&lt;/pre&gt;\n		&lt;p&gt;\n			4、行间跳转\n		&lt;/p&gt;\n&lt;pre&gt;    #G：跳转至第#行\n\n    gg: 第一行\n\n    G：最后一行\n&lt;/pre&gt;\n		&lt;p&gt;\n			5、末行模式\n		&lt;/p&gt;\n&lt;pre&gt;    .: 表示当前行\n\n    $: 最后一行\n\n    #：第#行\n\n    +#: 向下的#行\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			四、翻屏\n		&lt;/h3&gt;\n&lt;pre&gt;    Ctrl+f: 向下翻一屏\n	\n    Ctrl+b: 向上翻一屏\n\n    Ctrl+d: 向下翻半屏\n\n    Ctrl+u: 向上翻半屏\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			五、删除单个字符\n		&lt;/h3&gt;\n&lt;pre&gt;    x: 删除光标所在处的单个字符\n\n    #x: 删除光标所在处及向后的共#个字符\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			六、删除命令: d\n		&lt;/h3&gt;\n		&lt;p&gt;\n			d命令跟跳转命令组合使用\n		&lt;/p&gt;\n&lt;pre&gt;    #dw, #de, #db&lt;/pre&gt;\n		&lt;p&gt;\n			dd: 删除当前光标所在行&lt;br /&gt;\n#dd: 删除包括当前光标所在行在内的#行；\n		&lt;/p&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			七、粘贴命令 p\n		&lt;/h3&gt;\n&lt;pre&gt;    p: 如果删除或复制为整行内容，则粘贴至光标所在行的下方，如果复制或删除的内容为非整行，则粘贴至光标所在字符的后面\n\n    P: 如果删除或复制为整行内容，则粘贴至光标所在行的上方，如果复制或删除的内容为非整行，则粘贴至光标所在字符的前面\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			八、复制命令 y\n		&lt;/h3&gt;\n&lt;pre&gt;    用法同d命令&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			九、修改：先删除内容，再转换为输入模式\n		&lt;/h3&gt;\n&lt;pre&gt;    c: 用法同d命令&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十、替换：\n		&lt;/h3&gt;\n&lt;pre&gt;    r：单字符替换\n\n    #r: 光标后#个字符全部替换\n\n    R: 替换模式\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十一、撤消编辑操作 u\n		&lt;/h3&gt;\n&lt;pre&gt;    u：撤消前一次的编辑操作\n\n    #u: 直接撤消最近#次编辑操作\n\n    连续u命令可撤消此前的n次编辑操作\n\n    撤消最近一次撤消操作：Ctrl+r\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十二、重复前一次编辑操作\n		&lt;/h3&gt;\n&lt;pre&gt;    .：编辑模式重复前一次编辑操作&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十三、可视化模式\n		&lt;/h3&gt;\n&lt;pre&gt;    v: 按字符选取\n\n    V：按矩形选取&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十四、查找\n		&lt;/h3&gt;\n&lt;pre&gt;    /PATTERN\n\n    ?PATTERN\n\n    n 下一个\n\n    N 上一个\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十五、查找并替换\n		&lt;/h3&gt;\n		&lt;p&gt;\n			在末行模式下使用s命令\n		&lt;/p&gt;\n&lt;pre&gt;    headline,footlines#PATTERN#string#g\n\n    1,$:表示全文\n\n    %：表示全文\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十六、使用vim编辑多个文件\n		&lt;/h3&gt;\n&lt;pre&gt;    vim FILE1 FILE2 FILE3\n\n    :next 切换至下一个文件\n\n    :prev 切换至前一个文件\n\n    :last 切换至最后一个文件\n\n    :first 切换至第一个文件\n\n    :q退出当前文件\n\n    :qa 全部退出\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十七、分屏显示一个文件\n		&lt;/h3&gt;\n&lt;pre&gt;    Ctrl+w, s: 水平拆分窗口\n\n    Ctrl+w, v: 垂直拆分窗口\n&lt;/pre&gt;\n		&lt;p&gt;\n			在窗口间切换光标：\n		&lt;/p&gt;\n&lt;pre&gt;    Ctrl+w, ARROW(h,j,k,l或方向键) \n\n    :qa 关闭所有窗口\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十八、分窗口编辑多个文件\n		&lt;/h3&gt;\n&lt;pre&gt;    vim -o : 水平分割显示\n\n    vim -O : 垂直分割显示\n&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			十九、将当前文件中部分内容另存为另外一个文件\n		&lt;/h3&gt;\n		&lt;p&gt;\n			末行模式下使用w命令\n		&lt;/p&gt;\n&lt;pre&gt;    :ADDR1,ADDR2w /path/to/somewhere&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			二十、将另外一个文件的内容填充在当前文件中\n		&lt;/h3&gt;\n&lt;pre&gt;    :r /path/to/somefile\n\n    附加到当前文件光标后&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			二十一、跟shell交互\n		&lt;/h3&gt;\n&lt;pre&gt;    :! COMMAND&lt;/pre&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			二十二、高级话题\n		&lt;/h3&gt;\n		&lt;p&gt;\n			1、显示或取消显示行号\n		&lt;/p&gt;\n&lt;pre&gt;    :set nu\n\n    :set nonu\n\n    mu = number\n&lt;/pre&gt;\n		&lt;p&gt;\n			2、显示忽略或区分字符大小写\n		&lt;/p&gt;\n&lt;pre&gt;    :set ic\n\n    :set noic\n\n    ic = ignorecase\n&lt;/pre&gt;\n		&lt;p&gt;\n			3、设定自动缩进\n		&lt;/p&gt;\n&lt;pre&gt;    :set ai\n\n    :set noai\n\n    ai = autoindent\n&lt;/pre&gt;\n		&lt;p&gt;\n			4、查找到的文本高亮显示或取消\n		&lt;/p&gt;\n&lt;pre&gt;    :set hlsearch\n\n    :set nohlsearch\n&lt;/pre&gt;\n		&lt;p&gt;\n			5、语法高亮\n		&lt;/p&gt;\n&lt;pre&gt;    :syntax on\n\n    :syntax off&lt;/pre&gt;\n		&lt;div style=&quot;background-color:#DDDDDD;padding:10px;color:red;&quot;&gt;\n			注:特性当前有效，如果想要永久有效需修改配置文件\n		&lt;/div&gt;\n		&lt;h3 style=&quot;font-size:16px;&quot;&gt;\n			二十三、配置文件\n		&lt;/h3&gt;\n&lt;pre&gt;    /etc/vimrc    针对所有用户\n\n    ~/.vimrc    针对当前用户&lt;/pre&gt;\n	&lt;/div&gt;\n&lt;/div&gt;','1500033117','Linux','1126089177@qq.com','0'),(49,'debian8.2安装中文支持','&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;1、安装loacles&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;#apt-get install locales&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2、使用空格选择安装的语言&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;div class=&quot;crayon-line crayon-striped-line&quot; id=&quot;crayon-5917f3c1ccce7280358780-2&quot;&gt;\r\n	&lt;span class=&quot;crayon-v&quot; style=&quot;font-size:16px;&quot;&gt;#dpkg&lt;/span&gt;&lt;span class=&quot;crayon-o&quot; style=&quot;font-size:16px;&quot;&gt;-&lt;/span&gt;&lt;span class=&quot;crayon-e&quot; style=&quot;font-size:16px;&quot;&gt;reconfigure&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;crayon-v&quot; style=&quot;font-size:16px;&quot;&gt;locales&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;3、设置shell输出中文&lt;/span&gt;&lt;span&gt;&lt;/span&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;#export LANG=&quot;zh_CN.UTF-8&quot;。&lt;/span&gt;\r\n&lt;/p&gt;','1500033144','Linux','1126089177@qq.com','0'),(50,'Linux与window远程桌面控制','&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;1.安装rdesktop&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;sudo apt-get install rdesktop&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2.连接window&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;rdesktop&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp; -g 1024*768&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;ip地址&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;3.全屏连接：rdesktop -f ip地址（按ctrl+alt+enter退出全屏）&lt;/span&gt;\n&lt;/p&gt;','1500033168','Linux','1126089177@qq.com','0'),(51,'ajax提交post数据遇到大于小于等特殊符号丢失数据解决办法','&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;用ajax传输数据前先用js的encodeURIComponent()函数进行转义。&lt;/span&gt;','1500033188','ajax','1126089177@qq.com','0'),(53,'通过ajax发送post请求session失效原因','&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;原因是:&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;没有提交_token字段&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;解决办法是:&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;在提交的ajax参数中加入_token字段以及它的值&lt;/span&gt;\r\n&lt;/p&gt;','1500033238','laravel','1126089177@qq.com','0'),(56,'360浏览器显示页面混乱解决办法','&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;在html&amp;lt;head&amp;gt;标签中加入以下这句代码：&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;lt;meta http-equiv=&quot;X-UA-Compatible&quot; content=&quot;IE=edge&quot;&amp;gt;&lt;/span&gt;\n&lt;/p&gt;','1500432407','PHP开发经验','1126089177@qq.com','0'),(57,'git入门教程','&lt;p&gt;\n	&lt;span style=&quot;font-family:NSimSun;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;一、使用手册&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-family:SimHei;font-size:14px;&quot;&gt;有一下三种方法获取帮助：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-family:SimHei;font-size:14px;color:#E53333;&quot;&gt;$ git help &amp;lt;verb&amp;gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-family:SimHei;font-size:14px;color:#E53333;&quot;&gt;$ git &amp;lt;verb&amp;gt; --help&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;$ man git- &amp;lt;verb&amp;gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;例如，如果想获取config命令的手册，输入&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;$ git help config&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;$ git config --help&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;$man git-config&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:14px;color:#000000;font-family:SimHei;&quot;&gt;二、&lt;/span&gt;&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;&lt;span style=&quot;color:#000000;font-size:14px;font-family:SimHei;&quot;&gt;常用命令&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:16px;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;1.初始化本地仓库：&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;git init&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;font-size:14px;font-family:SimHei;&quot;&gt;&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;&lt;span style=&quot;color:#000000;font-size:14px;font-family:SimHei;&quot;&gt;例如，进入项目地址cd /var/www/html/git，然后输入git init&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:16px;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;2.创建文件：&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;vim git.test&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:16px;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;3.查看文件状态：&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;git status&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;(文件红色：git.test没有加入到git仓库管理中。文件绿色：git.test加入到git暂存区）&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:16px;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;4.添加git.test到git管理中：&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;git add git.test&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;5.添加当前目录所有文件到git管理：&lt;/span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;git add .&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;6.提交到仓库：git commit -m &quot;注释&quot;。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;三、配置用户名与邮箱&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;1.配置局部用户信息&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;配置用户名&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;git config user.name &quot;kevin&quot;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;配置用户邮箱&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;git config user.email &quot;15813246678@163.com&quot;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;2.配置全局用户信息&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;配置全局用户名&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;git config --global user.name &quot;kevin&quot;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;配置全局用户邮箱&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;git config --global user.email &quot;15813246678@163.com&quot;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;四、常用命令的别名&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;配置局部命令别名&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;1.给status命令起别名&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;git config alias.st &quot;status&quot;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;说明：alias.后面的st是原来命令status的别名&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;2.给commit起别名&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;git config alias.ci &amp;nbsp;&quot; commit -m&quot;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;起别名之后git ci test 相当于命令git commit -m &quot;test&quot;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;配置全局命令别名&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;color:#E53333;font-family:SimHei;&quot;&gt;git config --global alias.st &quot;status&quot;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;font-size:14px;font-family:SimHei;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;五.版本回退&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;如果文件被commit，使用&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;git reset --hard HEAD^&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;回退&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;color:#000000;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;1.版本回退：&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;git reset --hard HEAD^ 一个^代表一个版本&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;例如退回到上一个版本：git reset --hard HEAD^&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;退回到上两个版本：git reset --hard HEAD^^&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;2.退回到制定版本：&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;git reset --hard 8308f01 后面的&quot;8308f01&quot;为版本号&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;如果文件没被commit&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;退回到最高版本号：&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;git reset --hard HEAD&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;检出当前分支最高版本：&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;git checkout git.test 其中git.test为文件&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;七、查看修改日志&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;如果退回到早期的版本，那么后面的版本号用log命令将不能显示，需要使用&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;color:#E53333;font-size:14px;font-family:SimHei;&quot;&gt;git reflog&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:SimHei;&quot;&gt;命令查看每一次修改的日志&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;br /&gt;\n&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;','1501065976','Git','1126089177@qq.com','0'),(64,'Apache2配置虚拟主机','&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;deepin下Apache配置文件的目录位于/etc/apache2&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;首先编辑apache2.conf文件&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;将include model configuration后的两行注释去掉&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;然后打开sites-enabled文件夹，复制一份000-default.conf&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;打开修改如下(黄色部分)&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;lt;VirtualHost *:80&amp;gt;&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# The ServerName directive sets the request scheme, hostname and port that&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# the server uses to identify itself. This is used when creating&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# redirection URLs. In the context of virtual hosts, the ServerName&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# specifies what hostname must appear in the request\'s Host: header to&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# match this virtual host. For the default virtual host (this file) this&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# value is not decisive as it is used as a last resort host regardless.&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# However, you must set it for any further virtual host explicitly.&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;#域名&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;background-color:#FFE500;font-size:14px;&quot;&gt;ServerName tp.com&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;ServerAdmin webmaster@localhost&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;#运行目录&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;background-color:#FFE500;font-size:14px;&quot;&gt;DocumentRoot /var/www/html/tp/public&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# Available loglevels: trace8, ..., trace1, debug, info, notice, warn,&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# error, crit, alert, emerg.&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# It is also possible to configure the loglevel for particular&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# modules, e.g.&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;#LogLevel info ssl:warn&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;ErrorLog ${APACHE_LOG_DIR}/error.log&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;CustomLog ${APACHE_LOG_DIR}/access.log combined&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# For most configuration files from conf-available/, which are&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# enabled or disabled at a global level, it is possible to&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# include a line for only one particular virtual host. For example the&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# following line enables the CGI configuration for this host only&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;# after it has been globally disabled with &quot;a2disconf&quot;.&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;#Include conf-available/serve-cgi-bin.conf&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; &amp;lt;/VirtualHost&amp;gt;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; # vim: syntax=apache ts=4 sw=4 sts=4 sr noet&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;修改保存之后打开/etc/hosts文件&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;添加域名&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;127.0.0.1&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;localhost&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; 127.0.1.1 &amp;nbsp; cxwt-PC&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; 127.0.1.1 &amp;nbsp; &amp;nbsp; &amp;nbsp; jy.com&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; 0.0.0.0 account.jetbrains.com&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; 127.0.0.1 mynote.com&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;background-color:#FFE500;font-size:14px;&quot;&gt;127.0.0.1 tp.com&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; # The following lines are desirable for IPv6 capable hosts&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; ::1 &amp;nbsp; &amp;nbsp; ip6-localhost ip6-loopback&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; fe00::0 ip6-localnet&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; ff00::0 ip6-mcastprefix&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; ff02::1 ip6-allnodes&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px;&quot;&gt; ff02::2 ip6-allrouters&lt;/span&gt;\n&lt;/p&gt;\n&lt;div&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;保存修改后重启Apache&lt;/span&gt;\n&lt;/div&gt;\n&lt;br /&gt;\n&lt;div&gt;\n	&lt;br /&gt;\n&lt;/div&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;','1502868449','环境配置','1126089177@qq.com','0'),(66,'手机通过万能钥匙破解WiFi密码后查看密码','&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;安卓手机安装WiFi万能钥匙，可以破解已经分享的密码，但是如果手机没有root的话是查看不了密码的。root了之后可能手机就不再保修，再者也不想那么麻烦去安装root工具来root。初次之外还有一种办法，就是通过手机自带的备份工具，选择WiFi备份，备份完之后找到备份文件，然后通过电脑的编辑器分析出WiFi的密码。&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;br /&gt;\n&lt;/span&gt;\n&lt;/p&gt;','1503592819','加密与解密','1126089177@qq.com','0'),(68,'tty进入与退出','&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;1.如果本来在桌面，按&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;Ctrl+alt+F1~F6进入tty1~tty6&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;按Ctrl+alt+F7重新进入桌面&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;2.如果开机进入tty,重启桌面命令：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;sudo service lightdm restart&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;','1505404250','Linux','1126089177@qq.com','0'),(69,'crontab命令详解','&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;strong&gt;crontab命令：&lt;/strong&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;定时执行操作的命令，每一个用户拥有自己的crontab，配置文件存在/var目录下，不能被直接编辑&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;strong&gt;参数说明：&lt;/strong&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;-e设置定时器&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;-l列出当前计时器的设置&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;-r删除计时器的设置&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;-i交互模式，删除计时器设置时要先询问&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;strong&gt;书写规则&lt;/strong&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;15&amp;nbsp; &amp;nbsp; 9&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; 5&amp;nbsp; &amp;nbsp; root&amp;nbsp; &amp;nbsp; service apache2 start&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;第一列表示分钟1~59，每分钟用*或者*/1表示&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;第二列表示小时1~23(0表示0点）&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;第三列表示日期1~31&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;第四列表示月份1~12&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;第五列表示标识号星期几0～6(0表示星期天）&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;第六列表示要运行的命令&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;strong&gt;crontab书写规则的例子：&lt;/strong&gt;&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;30&amp;nbsp; &amp;nbsp; 21&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; service apache2 restart&amp;nbsp; &amp;nbsp; ……表示每晚的21:30重启Apache&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;45&amp;nbsp; &amp;nbsp; 4&amp;nbsp; &amp;nbsp; 1&amp;nbsp; &amp;nbsp; 1,10,22&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; ……表示每月的1、10、22日的4:45分执行指定操作&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;10&amp;nbsp; &amp;nbsp; 1&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; 6,0&amp;nbsp; &amp;nbsp; ……表示每周六、日的1:10执行指定操作&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;0,30&amp;nbsp; &amp;nbsp; 18-23&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; ……表示每天的18:00~23:00之间间隔30分钟执行指定操作&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;0&amp;nbsp; &amp;nbsp; 23&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; 6&amp;nbsp; &amp;nbsp; ……表示每个星期六的23点执行指定操作&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;0&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; ……表示每隔一个小时执行指定操作&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;0&amp;nbsp; &amp;nbsp; 11&amp;nbsp; &amp;nbsp; 4&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; mon-wed&amp;nbsp; &amp;nbsp; ……每个月的4号与每周一到周三的11点执行指定操作&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;0&amp;nbsp; &amp;nbsp; 4&amp;nbsp; &amp;nbsp; 1&amp;nbsp; &amp;nbsp; jan&amp;nbsp; &amp;nbsp; *&amp;nbsp; &amp;nbsp; ……一月1号的4点执行指定操作&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;','1505459104','Linux','1126089177@qq.com','0'),(70,'deepin下mysql重装失败','&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;执行了apt-get autoremove mysql-server后发现只删除了一百七十多K的东西。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;接着执行apt-get install mysql-server后，也是下载了一百七十多k的东西，安装之后不能启动。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;后来发现需要在后面加上版本才能安装，于是执行apt-get install mysql-server-5.7&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;结果可以安装，但是配置会出错。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;最后在网上找到了解决的办法：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;1.删除指定版本：apt-get autoremove mysql-server-5.7&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;2.删除安装的所有mysql服务：sudo&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt; apt-get remove --purge mysql-\\*&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;3.然后重新安装：apt-get install mysql-server-5.7 mysql-client-5.7&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=&quot;font-size:14px;&quot;&gt;4.安装完成后重启mysql:service mysql restart&lt;/span&gt;&lt;br /&gt;\n&lt;span id=&quot;__kindeditor_bookmark_start_5__&quot;&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \n&lt;/p&gt;','1505821100','环境配置','1126089177@qq.com','0'),(71,'12312313','12313123123','1506573450','common','testbugs@vip.qq.com','0'),(72,'1231233333333333333','33333333333333333333','1506574139','123123','testbugs@vip.qq.com','0');
